def make_coeffs(coeffs):
    """
    Pass in array of coeffs, returns int which represents all bits of coeffs to be passed into parameterized FIR as well as number of coeffs. This is really to just make things easier for me.
    """
    coeffs_bytes = b''
    for c in coeffs:
        if c >= 0: coeffs_bytes += int(c).to_bytes()
        else: coeffs_bytes += int(256 + c).to_bytes()
    return int.from_bytes(coeffs_bytes, "big"), len(coeffs)

